static constexpr size_t NUM_FORBIDDEN_W_PATTERNS = 100;

// O(1) lookup table for forbidden W patterns indexed by (aa_before, aa_after)
// Returns r_ratio (0.0f if not forbidden)
struct ForbiddenWLookup {
    // Map AA chars to compact indices (0-25 for A-Z, 26 for invalid)
    static constexpr int aa_index(char c) {
        return (c >= 'A' && c <= 'Z') ? (c - 'A') : 26;
    }
    float table[27][27];

    constexpr ForbiddenWLookup() : table{} {
        for (size_t i = 0; i < NUM_FORBIDDEN_W_PATTERNS; ++i) {
            int bi = aa_index(FORBIDDEN_W_PATTERNS[i].aa_before);
            int ai = aa_index(FORBIDDEN_W_PATTERNS[i].aa_after);
            if (bi < 27 && ai < 27) {
                table[bi][ai] = FORBIDDEN_W_PATTERNS[i].r_ratio;
            }
        }
    }

    float lookup(char aa_before, char aa_after) const {
        int bi = aa_index(aa_before);
        int ai = aa_index(aa_after);
        if (bi >= 27 || ai >= 27) return 0.0f;
        return table[bi][ai];
    }
};

static constexpr ForbiddenWLookup FORBIDDEN_W_LOOKUP{};

// ============================================================================
// AA-impact codon substitution table
// For each codon x position x damage type, does the damage change the AA?
// Uses TCAG encoding (T=0, C=1, A=2, G=3) matching codon_to_idx() convention.
// ============================================================================

// Map nucleotide char to TCAG index (constexpr-safe)
static constexpr int nuc_index(char c) {
    return (c == 'T' || c == 't') ? 0 :
           (c == 'C' || c == 'c') ? 1 :
           (c == 'A' || c == 'a') ? 2 :
           (c == 'G' || c == 'g') ? 3 : -1;
}

// Compute codon index from three nucleotide TCAG indices
static constexpr int codon_index(int c0, int c1, int c2) {
    return (c0 << 4) | (c1 << 2) | c2;
}

// Compute codon index from three nucleotide characters
static constexpr int codon_index_from_chars(char c0, char c1, char c2) {
    return codon_index(nuc_index(c0), nuc_index(c1), nuc_index(c2));
}

struct AAImpactLookup {
    // Standard genetic code in TCAG order
    static constexpr char GENETIC_CODE[64] = {
        'F','F','L','L', 'S','S','S','S', 'Y','Y','*','*', 'C','C','*','W',  // T__
        'L','L','L','L', 'P','P','P','P', 'H','H','Q','Q', 'R','R','R','R',  // C__
        'I','I','I','M', 'T','T','T','T', 'N','N','K','K', 'S','S','R','R',  // A__
        'V','V','V','V', 'A','A','A','A', 'D','D','E','E', 'G','G','G','G',  // G__
    };

    // table[codon_idx][position][damage_type]
    // damage_type: 0 = C->T (deamination at 5'), 1 = G->A (complementary at 3')
    // value: 1 if AA changes, 0 if synonymous or base not susceptible
    uint8_t table[64][3][2];

    constexpr AAImpactLookup() : table{} {
        for (int c0 = 0; c0 < 4; ++c0) {
            for (int c1 = 0; c1 < 4; ++c1) {
                for (int c2 = 0; c2 < 4; ++c2) {
                    int idx = (c0 << 4) | (c1 << 2) | c2;
                    char orig_aa = GENETIC_CODE[idx];
                    int bases[3] = {c0, c1, c2};

                    for (int pos = 0; pos < 3; ++pos) {
                        // C->T (type 0): only if base is C (TCAG index 1)
                        if (bases[pos] == 1) {
                            int mut[3] = {bases[0], bases[1], bases[2]};
                            mut[pos] = 0; // C(1) -> T(0)
                            int mut_idx = (mut[0] << 4) | (mut[1] << 2) | mut[2];
                            table[idx][pos][0] = (GENETIC_CODE[mut_idx] != orig_aa) ? 1 : 0;
                        }
                        // G->A (type 1): only if base is G (TCAG index 3)
                        if (bases[pos] == 3) {
                            int mut[3] = {bases[0], bases[1], bases[2]};
                            mut[pos] = 2; // G(3) -> A(2)
                            int mut_idx = (mut[0] << 4) | (mut[1] << 2) | mut[2];
                            table[idx][pos][1] = (GENETIC_CODE[mut_idx] != orig_aa) ? 1 : 0;
                        }
                    }
                }
            }
        }
    }

    constexpr uint8_t aa_impact(int codon_idx, int pos, int damage_type) const {
        return table[codon_idx][pos][damage_type];
    }
};

static constexpr AAImpactLookup AA_IMPACT_LOOKUP{};

